const response = $input.first().json;
const datos = $('Construir Prompt v8.2').first().json;

try {
  const articulo = JSON.parse(response.choices[0].message.content);

  if (!articulo.titulo || !articulo.contenido || !articulo.metaDescripcion) {
    throw new Error('Campos requeridos faltantes');
  }

  if (articulo.contenido.length < 5000) {
    throw new Error('Contenido insuficiente: ' + articulo.contenido.length + ' chars (mÃ­nimo 5000)');
  }

  // Validar que use las imÃ¡genes correctas
  const img2 = datos.imagen2;
  const img3 = datos.imagen3;
  const img4 = datos.imagen4;

  let contenido = articulo.contenido;

  // Si GPT no incluyÃ³ las imÃ¡genes, agregarlas en los lugares correctos
  if (!contenido.includes(img2)) {
    contenido = contenido.replace(
      '</section>\n\n<section class="article-section" id="perfil-corporativo">',
      `</section>\n\n<figure class="article-image"><img src="../img/seguridad-privada/${img2}" alt="Seguridad privada profesional - ${datos.empresa.nombre}" loading="lazy"><figcaption>Servicios profesionales de seguridad privada en MÃ©xico</figcaption></figure>\n\n<section class="article-section" id="perfil-corporativo">`
    );
  }

  return {
    valido: true,
    titulo: articulo.titulo.substring(0, 60),
    tituloCorto: articulo.tituloCorto || articulo.titulo.substring(0, 25),
    metaDescripcion: articulo.metaDescripcion.substring(0, 160),
    keywords: articulo.keywords,
    tiempoLectura: articulo.tiempoLectura || 20,
    subtitulo: articulo.subtitulo,
    imagenAlt: articulo.imagenAlt || datos.empresa.nombre + ' - Seguridad Privada MÃ©xico',
    tocItems: articulo.tocItems || [],
    faqs: articulo.faqs || [],
    contenidoHTML: contenido,
    contenidoLength: contenido.length,
    slugArticulo: datos.slugArticulo,
    empresa: datos.empresa,
    tipoArticulo: datos.tipoArticulo,
    imagenPrincipal: datos.imagenPrincipal,
    imagen2: datos.imagen2,
    imagen3: datos.imagen3,
    imagen4: datos.imagen4
  };
} catch (e) {
  return {
    valido: false,
    error: e.message,
    rawResponse: response.choices?.[0]?.message?.content?.substring(0, 500)
  };
}

const articulo = $('Construir HTML + Footer').first().json;
const githubFile = $input.first().json;

let jsonData;
try {
  const content = Buffer.from(githubFile.content.replace(/\n/g, ''), 'base64').toString('utf-8');
  jsonData = JSON.parse(content);
} catch (e) {
  jsonData = { version: '3.0', articulos: [] };
}

const nuevoArticulo = {
  id: articulo.slugArticulo,
  titulo: articulo.titulo,
  slug: articulo.slugArticulo,
  categoria: 'seguridad-privada',
  categoriaNombre: 'Seguridad Privada',
  extracto: articulo.extractoBlog,
  imagen: `img/seguridad-privada/${articulo.imagenPrincipal}`,
  tiempoLectura: articulo.tiempoLectura,
  destacado: false,
  n8n: true,
  version: '8.2',
  empresa: articulo.empresaNombre,
  empresaSlug: articulo.empresaSlug,
  keywords: articulo.keywords,
  contenidoLength: articulo.contenidoLength,
  faqCount: articulo.faqCount,
  imagenesUsadas: articulo.imagenesUsadas
};

const existeIndex = jsonData.articulos.findIndex(a => a.id === nuevoArticulo.id);
if (existeIndex >= 0) {
  jsonData.articulos[existeIndex] = nuevoArticulo;
} else {
  jsonData.articulos.unshift(nuevoArticulo);
}

jsonData.version = '3.0';

return {
  jsonActualizado: JSON.stringify(jsonData, null, 2),
  sha: githubFile.sha,
  articulo: articulo,
  nuevoArticulo: nuevoArticulo,
  totalArticulos: jsonData.articulos.length
};

const datos = $('Actualizar JSON').first().json;
const a = datos.articulo;

return {
  exito: true,
  empresa: a.empresaNombre,
  titulo: a.titulo,
  urlArticulo: `https://origenlab.com/blog/${a.slugArticulo}.html`,
  urlPerfil: `https://origenlab.com/categorias/seguridad-privada/${a.empresaSlug}.html`,
  tiempoLectura: `${a.tiempoLectura} min`,
  contenidoLength: `${a.contenidoLength} chars`,
  faqCount: a.faqCount,
  totalArticulos: datos.totalArticulos,
  imagenes: a.imagenesUsadas.join(', '),
  mensaje: 'âœ… ArtÃ­culo v8.2 SEO PRO publicado'
};

