const response = $input.first().json;
const datos = $('Construir Prompt').first().json;

try {
  if (!response.choices || !response.choices[0] || !response.choices[0].message) {
    throw new Error('Respuesta OpenAI invalida');
  }

  var art = JSON.parse(response.choices[0].message.content);

  if (!art.titulo) throw new Error('Falta titulo');
  if (!art.contenido) throw new Error('Falta contenido');
  if (art.contenido.length < 5000) throw new Error('Contenido muy corto: ' + art.contenido.length + ' chars (minimo 5000)');

  return [{
    json: {
      valido: true,
      titulo: String(art.titulo).substring(0, 60),
      tituloCorto: String(art.tituloCorto || art.titulo).substring(0, 25),
      metaDescripcion: String(art.metaDescripcion || '').substring(0, 160),
      keywords: String(art.keywords || 'seguridad privada Mexico'),
      tiempoLectura: Number(art.tiempoLectura) || 25,
      subtitulo: String(art.subtitulo || ''),
      imagenAlt: String(art.imagenAlt || datos.img1.alt),
      tocItems: Array.isArray(art.tocItems) ? art.tocItems : [],
      faqs: Array.isArray(art.faqs) ? art.faqs : [],
      contenidoHTML: String(art.contenido),
      contenidoLength: art.contenido.length,
      slug: String(datos.slug),
      empresa: datos.empresa,
      img1: datos.img1,
      img2: datos.img2,
      img3: datos.img3
    }
  }];
} catch (err) {
  var rawContent = 'Sin contenido';
  if (response.choices && response.choices[0] && response.choices[0].message) {
    rawContent = String(response.choices[0].message.content).substring(0, 500);
  }
  return [{
    json: {
      valido: false,
      error: String(err.message),
      raw: rawContent
    }
  }];
}

var htmlData = $('Construir HTML').first().json;
var checkResult = $input.first().json;

var fileSha = '';
if (checkResult && checkResult.sha && typeof checkResult.sha === 'string') {
  fileSha = checkResult.sha;
}

return [{
  json: {
    htmlBase64: htmlData.htmlBase64,
    ruta: htmlData.ruta,
    titulo: htmlData.titulo,
    extracto: htmlData.extracto,
    tiempoLectura: htmlData.tiempoLectura,
    slug: htmlData.slug,
    imgPrincipal: htmlData.imgPrincipal,
    empresaNombre: htmlData.empresaNombre,
    empresaSlug: htmlData.empresaSlug,
    keywords: htmlData.keywords,
    contenidoLength: htmlData.contenidoLength,
    faqCount: htmlData.faqCount,
    fileSha: fileSha
  }
}];

var artData = $('Preparar Upload').first().json;
var fileData = $input.first().json;

var articulos = {version: '3.0', articulos: []};

try {
  var decoded = Buffer.from(fileData.content.replace(/\n/g, ''), 'base64').toString('utf-8');
  articulos = JSON.parse(decoded);
} catch (e) {
  articulos = {version: '3.0', articulos: []};
}

var nuevo = {
  id: artData.slug,
  titulo: artData.titulo,
  slug: artData.slug,
  categoria: 'seguridad-privada',
  categoriaNombre: 'Seguridad Privada',
  extracto: artData.extracto,
  imagen: 'img/seguridad-privada/' + artData.imgPrincipal,
  tiempoLectura: artData.tiempoLectura,
  destacado: false,
  n8n: true,
  version: '8.9',
  empresa: artData.empresaNombre,
  empresaSlug: artData.empresaSlug,
  keywords: artData.keywords,
  contenidoLength: artData.contenidoLength,
  faqCount: artData.faqCount
};

var existingIndex = -1;
for (var i = 0; i < articulos.articulos.length; i++) {
  if (articulos.articulos[i].id === nuevo.id) {
    existingIndex = i;
    break;
  }
}

if (existingIndex >= 0) {
  articulos.articulos[existingIndex] = nuevo;
} else {
  articulos.articulos.unshift(nuevo);
}

var jsonBase64 = Buffer.from(JSON.stringify(articulos, null, 2), 'utf-8').toString('base64');

return [{
  json: {
    jsonBase64: jsonBase64,
    sha: fileData.sha,
    slug: artData.slug,
    titulo: artData.titulo,
    empresaNombre: artData.empresaNombre,
    contenidoLength: artData.contenidoLength,
    faqCount: artData.faqCount,
    total: articulos.articulos.length
  }
}];

var data = $('Update JSON').first().json;

var mensaje = 'ARTICULO v8.9 PUBLICADO' + '\n\n' +
  'Empresa: ' + data.empresaNombre + '\n' +
  'Titulo: ' + data.titulo + '\n\n' +
  'Contenido: ' + data.contenidoLength + ' chars' + '\n' +
  'FAQs: ' + data.faqCount + '\n' +
  'Total: ' + data.total + ' articulos' + '\n\n' +
  'URL: https://origenlab.com/blog/' + data.slug + '.html';

return [{
  json: {
    mensaje: mensaje
  }
}];
