const response = $input.first().json;
const datos = $('Construir Prompt').first().json;

try {
  const articulo = JSON.parse(response.choices[0].message.content);
  if (!articulo.titulo || !articulo.contenido) throw new Error('Campos faltantes');

  let html = articulo.contenido;
  const imgs = datos.imagenesContenido;
  html = html.replace(/\{\{IMAGEN_1\}\}/g, `<figure class="article-image"><img src="../img/seguridad-privada/${imgs[0]}" alt="${articulo.titulo}" loading="lazy"></figure>`);
  html = html.replace(/\{\{IMAGEN_2\}\}/g, `<figure class="article-image"><img src="../img/seguridad-privada/${imgs[1]}" alt="Servicios" loading="lazy"></figure>`);
  html = html.replace(/\{\{IMAGEN_3\}\}/g, `<figure class="article-image"><img src="../img/seguridad-privada/${imgs[2]}" alt="Seguridad" loading="lazy"></figure>`);

  return {
    valido: true,
    titulo: articulo.titulo,
    metaDescripcion: articulo.metaDescripcion,
    palabrasClave: articulo.palabrasClave || [],
    tiempoLectura: articulo.tiempoLectura || 10,
    contenidoHTML: html,
    extractoBlog: articulo.extractoBlog || articulo.metaDescripcion,
    tocItems: articulo.tocItems || [],
    slugArticulo: datos.slugArticulo,
    imagenPrincipal: datos.imagenPrincipal,
    empresa: datos.empresa,
    urlPerfil: datos.urlPerfil
  };
} catch (e) {
  return { valido: false, error: e.message };
}

const articulo = $('Construir HTML').first().json;
const githubFile = $input.first().json;

// Decodificar JSON actual
let jsonData;
try {
  const content = Buffer.from(githubFile.content.replace(/\n/g, ''), 'base64').toString('utf-8');
  jsonData = JSON.parse(content);
} catch (e) {
  jsonData = { version: '1.0', lastUpdated: new Date().toISOString(), articulos: [] };
}

// Crear nuevo art√≠culo
const nuevoArticulo = {
  id: articulo.slugArticulo,
  titulo: articulo.titulo,
  slug: articulo.slugArticulo,
  categoria: 'seguridad-privada',
  categoriaNombre: 'Seguridad Privada',
  extracto: articulo.extractoBlog,
  imagen: `img/seguridad-privada/${articulo.imagenPrincipal}`,
  tiempoLectura: articulo.tiempoLectura,
  destacado: false,
  fecha: new Date().toISOString().split('T')[0],
  n8n: true,
  empresa: articulo.empresaNombre
};

// Verificar si ya existe
const existe = jsonData.articulos.some(a => a.id === nuevoArticulo.id);
if (!existe) {
  jsonData.articulos.unshift(nuevoArticulo);
  jsonData.lastUpdated = new Date().toISOString();
}

return {
  jsonActualizado: JSON.stringify(jsonData, null, 2),
  sha: githubFile.sha,
  articulo: articulo,
  nuevoArticulo: nuevoArticulo
};

const datos = $('Actualizar JSON').first().json;
const a = datos.articulo;

return {
  exito: true,
  empresa: a.empresaNombre,
  titulo: a.titulo,
  urlArticulo: `https://origenlab.com/${a.rutaArchivo}`,
  urlPerfil: a.urlPerfil,
  tiempoLectura: `${a.tiempoLectura} min`,
  mensaje: 'Art√≠culo publicado y JSON actualizado'
};
